import React, { Component } from 'react';
import './App.css';
import AppCarsContainer from './components/AppCarsContainer.js';
import axios from 'axios';
import Search from 'react-search'

class App extends Component {
  state = {
    cars: [],
  }

  componentDidMount(){
    let carsArray = [];
    axios.get('https://gist.githubusercontent.com/scottburton11/66a921c458f9500a773a6b0ac65006df/raw/629bfd6a3125e3428bd85a53231bd8018c407a65/Javascript%2520Working%2520With%2520Data%2520Challenge%2520data')
      .then(res => res.data.map((element,i) => {
          let curObj = {};
          //Using react-search
          //https://www.npmjs.com/package/react-search
          //In order to make it work data has to be organized with "id" and "value"

          curObj["id"] = i;
          curObj["value"] = element["Make"];
          curObj["car"] = element;
          carsArray.push(curObj);
          this.setState(prevState => ({
            cars: [...prevState.cars, curObj]
          }))
        })
      )

  }
  _carBrandSearch(items) {
    console.log(items)
  }
  render() {
    return (
      <div className="App">
          <h1>
            React Car Detail Assignment
          </h1>
          <Search items={this.state.cars}
                placeholder='Choose Vehicle Make'
                multiple={true}
                onItemsChanged={this._carBrandSearch.bind(this)} />
          <AppCarsContainer cars={this.state.cars}/>

      </div>
    );
  }
}

export default App;
